mapper.delete:
DELETE FROM backlog WHERE sn IN (SELECT sn from tk_ticket WHERE category='XX.XXXXXXX.FHCY' AND del_flag = 0

AND content IN

        #{content}

)

DELETE tk, tkl, tkt, tkp FROM tk_ticket tk LEFT JOIN tk_ticket_log tkl ON tk.id = tkl.ticket_id AND tkl.del_flag=0 LEFT JOIN tk_ticket_gcjc tkt ON tk.id = tkt.ticket_id AND tkt.del_flag=0 LEFT JOIN tk_ticket_processor tkp ON tk.id = tkp.ticket_id AND tkp.del_flag=0 WHERE tk.del_flag=0 AND tk.category = ${category}
AND tk.content IN
#{content}

mapper.select:
SELECT
ci3.`code`
FROM
check_item ci3
LEFT JOIN check_item ci2 ON ci2.id = ci3.parent_id AND ci2.del_flag = 0
LEFT JOIN check_item ci1 ON ci1.id = ci2.parent_id AND ci1.del_flag = 0
WHERE ci3.check_list_id = #{checkListId}
AND ci3.`enable` = 1
AND ci3.del_flag = 0
AND ci3.`level` = 3

    AND ci1.`name` = #{firstCheckName}


    AND ci2.`name` = #{secondCheckName}


    AND ci3.`name` = #{lastCheckName}


    AND ci3.`code` = #{pathCode}

SELECT r.unit, r.floor, r.ban_code AS banCode, r.ban_name AS banName, r.room_code AS roomCode, r.room_name AS roomName, ht.image_url AS checkImageUrl, ht.id AS houseTypeId, ps.id AS projectSiteId, ps.part_name AS projectSiteName FROM check_batch_room cbr LEFT JOIN room r ON cbr.room_code = r.room_code AND r.del_flag = 0 LEFT JOIN house_type ht ON ht.id = r.check_house_type_id AND ht.del_flag = 0 LEFT JOIN house_relevance hr ON hr.house_id = ht.id AND hr.del_flag=0 LEFT JOIN project_site ps ON ps.division_position_id = hr.division_position_id AND ps.del_flag=0 WHERE cbr.check_batch_id=#{batchId} AND cbr.del_flag = 0
AND r.ban_name = #{banName}
AND r.unit = #{unitName}
AND r.floor = #{floorName}
AND r.rooms = #{roomName}

SELECT GROUP_CONCAT(t.id) AS ticketIds, tp.processor_id AS processorId, t.`status` FROM tk_ticket_processor tp LEFT JOIN tk_ticket t ON t.id = tp.ticket_id AND t.del_flag = 0 WHERE 1 = 1
AND tp.ticket_id IN
#{ticketId}
AND tp.role_type = #{roleCode}
AND t.`status` = #{ticketStatus}
AND tp.del_flag =0 GROUP BY tp.processor_id

SELECT check_batch_id AS batchId, GROUP_CONCAT( id ) AS ticketIds FROM tk_ticket WHERE 1 =1
AND id IN
#{ticketId}
AND `status` = #{ticketStatus}
GROUP BY batchId

SELECT t.id AS ticketId FROM tk_ticket t LEFT JOIN tk_ticket_processor tp ON t.id = tp.ticket_id AND tp.del_flag = 0 WHERE 1 = 1
AND t.stage_code=#{stageCode}
AND t.`status` = #{ticketStatus}
AND t.check_batch_id = #{batchId}
AND tp.processor_id=#{processorId}
AND t.del_flag=0 GROUP BY t.id

SELECT tp.processor_id AS processorId FROM tk_ticket_processor tp LEFT JOIN tk_ticket t ON t.id = tp.ticket_id AND tp.del_flag = 0 WHERE 1 = 1
AND t.stage_code=#{stageCode}
AND t.`status` = #{ticketStatus}
AND t.check_batch_id = #{batchId}
AND tp.role_type = 1 AND t.del_flag = 0 GROUP BY tp.processor_id
