mapper.select:
SELECT ze.id AS sectionId, ze.`name` AS sectionName, ze.org_code AS orgCode, ze.org_name AS orgName, ze.project_code AS projectCode, ze.project_name AS projectName, ze.stage_code AS stageCode, ze.stage_name AS stageName, GROUP_CONCAT( zsc.construction ) AS construction, GROUP_CONCAT( zsc.construction_guid ) AS constructionGuid, ze.contractor, ze.contractor_guid AS contractorGuid, ze.supervisor, ze.supervisor_guid AS supervisorGuid, ze.type AS sectionType, ze.picture AS sectionPicture FROM zxxj_section_construction zsc LEFT JOIN zxxj_section ze ON zsc.section_id = ze.id WHERE ze.del_flag = 0
AND ze.`name` = #{sectionName}
AND ze.id = #{sectionId}
AND type = #{sectionType}

SELECT r.ban_name AS banName, r.ban_code AS banCode, CONCAT_WS( '-', r.ban_name, CONCAT( r.floor, '层' ) ) AS partName, r.floor AS partCode FROM room r WHERE r.del_flag = 0 AND r.ban_code IN ( SELECT ban_code FROM zxxj_section_scope WHERE 1 = 1 AND section_id = #{sectionId}) AND r.floor NOT IN (SELECT part_code FROM process_detail WHERE del_flag = 0 AND check_id = #{checkId} AND section_id = #{sectionId})
AND r.unit IN ( SELECT unit FROM zxxj_section_scope WHERE 1 = 1 AND section_id = #{sectionId} )
AND r.ban_name = #{roomQuery.banName}
AND r.floor = #{roomQuery.floorName}
GROUP BY partCode

SELECT r.banName, r.banCode, r.partName, r.partCode FROM ( SELECT ban_name AS banName, ban_code AS banCode, CASE WHEN unit = '' or unit is null THEN CONCAT_WS( '-', ban_name, CONCAT( floor, '层' ) ) ELSE CONCAT_WS( '-', ban_name, CONCAT( unit, '单元' ), CONCAT( floor, '层' ) ) END AS partName, CASE WHEN unit = '' or unit is null THEN floor ELSE CONCAT_WS( '&', CONCAT_WS( '-', ban_code, unit ), floor ) END AS partCode FROM room WHERE del_flag = 0 AND ban_code IN ( SELECT ban_code FROM zxxj_section_scope WHERE 1 = 1 AND section_id = #{sectionId} )
AND unit IN ( SELECT unit FROM zxxj_section_scope WHERE 1 = 1 AND section_id = #{sectionId} )
AND ban_name = #{roomQuery.banName}
AND floor = #{roomQuery.floorName}
AND unit = #{roomQuery.unitName}
GROUP BY partName ) r WHERE r.partCode NOT IN (SELECT part_code FROM process_detail WHERE del_flag=0 AND check_id = #{checkId} AND section_id = #{sectionId}) OR r.partName NOT IN (SELECT part_name FROM process_detail WHERE del_flag=0 AND check_id = #{checkId} AND section_id = #{sectionId})

SELECT r.banName, r.banCode, r.partName, r.partCode FROM ( SELECT ban_name AS banName, ban_code AS banCode, CASE WHEN unit = '' or unit is null THEN CONCAT_WS( '&', ban_code, floor, room_code ) ELSE CONCAT_WS( '&', ban_code, CONCAT_WS( '-', ban_code, unit ), floor, room_code ) END AS partCode, CASE WHEN unit = '' or unit is null THEN CONCAT_WS( '-', ban_name, CONCAT( floor, '层' ), rooms ) ELSE CONCAT_WS( '-', ban_name, CONCAT( unit, '单元' ), CONCAT( floor, '层' ), rooms ) END AS partName FROM room WHERE del_flag = 0 AND ban_code IN ( SELECT ban_code FROM zxxj_section_scope WHERE 1 = 1 AND section_id = #{sectionId})
AND unit IN ( SELECT unit FROM zxxj_section_scope WHERE 1 = 1 AND section_id = #{sectionId} )
AND ban_name = #{roomQuery.banName}
AND floor = #{roomQuery.floorName}
AND unit = #{roomQuery.unitName}
AND rooms = #{roomQuery.roomName}
GROUP BY partCode) r WHERE r.partCode NOT IN (SELECT part_code FROM process_detail WHERE del_flag=0 AND check_id = #{checkId} AND section_id = #{sectionId})

SELECT part_name AS partName, part_code AS partCode FROM process_custom_check_batch WHERE del_flag = 0
AND check_id = #{checkId}
AND section_id = #{sectionId}

SELECT
user_id AS userId
FROM
check_batch_user
WHERE
del_flag = 0

    AND stage_code = #{stageCode}


    AND role_type = #{roleType}


    AND category = #{category}

SELECT user_id AS userId, user_code AS userName, real_name AS realName FROM check_batch_user WHERE del_flag = 0
AND stage_code = #{stageCode}
AND role_type = #{roleType}
AND category = #{category}

SELECT cf.detail_id AS detailId, cf.flow_id AS flowId, cf.node, cf.if_appoint AS ifAppoint, cf.role_type AS roleType, cf.`status` FROM process_detail_check_flow cf WHERE cf.del_flag = 0 AND cf.detail_id = #{detailId}
AND cf.flow_id = #{flowId}
AND cf.node = #{node}

SELECT cf.detail_id AS detailId, cf.flow_id AS flowId, cf.node, cf.if_appoint AS ifAppoint, cf.role_type AS roleType, cf.`status`, h.next_flow_id AS nextFlowId, h.user_id AS userId, h.real_name AS realName, h.company_guid AS companyGuid, h.company_name AS companyName FROM process_detail_check_flow cf LEFT JOIN process_detail_handler h ON h.next_flow_id = cf.flow_id AND h.del_flag=0 WHERE cf.del_flag = 0 AND cf.detail_id = #{detailId} AND h.detail_id = #{detailId}
AND cf.flow_id = #{flowId}
AND cf.node = #{node}

SELECT id FROM process_detail t WHERE 1 = 1

    and t.org_name = #{detailQuery.orgName}


    and t.project_name = #{detailQuery.projectName}


    and t.stage_name = #{detailQuery.stageName}


    and t.check_type = #{detailQuery.checkType}


    and t.section_name = #{detailQuery.sectionName}


    and t.check_path_name like concat('%',#{detailQuery.checkPathName},'%')


    and t.part_name like concat('%',#{detailQuery.partName},'%')


    and t.id in (
    select cf.detail_id from process_detail_check_flow cf where
    cf.company_name like concat('%',#{detailQuery.submitCompanyName},'%') and cf.role_type=1)


    and t.id in (
    select cf.detail_id from process_detail_check_flow cf where
    cf.company_name like concat('%',#{detailQuery.acceptCompanyName},'%') and cf.role_type=2)


    and t.id in (
    select cf.detail_id from process_detail_check_flow cf where
    cf.real_name like concat('%',#{detailQuery.submitUser},'%') and cf.node=1)


    and t.id in (
    select cf.detail_id from process_detail_check_flow cf where
    cf.real_name like concat('%',#{detailQuery.acceptUser},'%') and cf.node=2)


    and t.id in (
    select cf.detail_id from process_detail_check_flow cf where
    cf.real_name like concat('%',#{detailQuery.checkUser},'%') and cf.node=3)


    and t.report_time > #{detailQuery.submitTimeStart}


    and t.report_time < #{detailQuery.submitTimeEnd}


    and t.inspection_time > #{detailQuery.acceptTimeStart}


    and t.inspection_time < #{detailQuery.acceptTimeEnd}


    and t.spot_check_time > #{detailQuery.checkTimeStart}


    and t.spot_check_time < #{detailQuery.checkTimeEnd}


    and t.status in

        #{item}


and t.del_flag = 0
order by t.create_date desc

SELECT r.ban_name AS banName, r.ban_code AS banCode, r.unit, r.floor, r.rooms, r.room_name AS roomName, r.room_code AS roomCode, ht.image_url AS checkImageUrl, ht.id AS houseTypeId FROM room r LEFT JOIN house_type ht ON ht.id = r.project_house_type_id AND ht.del_flag = 0 WHERE r.del_flag = 0 AND r.ban_code IN ( SELECT ban_code FROM zxxj_section_scope WHERE 1 = 1 AND section_id = 1498845212762828801 )
AND unit IN ( SELECT unit FROM zxxj_section_scope WHERE 1 = 1 AND section_id = #{sectionId} )
AND r.ban_name = #{roomQuery.banName}
AND r.unit = #{roomQuery.unitName}
AND r.floor = #{roomQuery.floorName}
AND r.rooms = #{roomQuery.roomName}
AND ht.check_type=1 GROUP BY r.room_code

SELECT * FROM process_detail_issue WHERE detail_id = #{detailId}
AND `status` IN
#{status}

SELECT zs.* from process_detail pd left join process_detail_issue pdi ON pdi.detail_id = pd.id AND pdi.del_flag=0
left join zxxj_section zs on zs.id = pd.section_id AND zs.del_flag=0 WHERE pd.del_flag=0

    AND pdi.id=#{issueId}


    AND pd.id=#{detailId}
