mapper.select:
SELECT cis.batch_id AS batchId, cis.score, cis.score_info AS scoreInfo, tci.checkItemId, tci.checkItemName, tci.parentId, tci.templateId, tci.`level`, tci.redLine, tci.extension FROM ( SELECT
FROM zxxj_template_check_item WHERE del_flag = 0 AND extension != '' AND extension IS NOT NULL
AND template_id = #{templateId}
AND id = #{checkItemId}
) tci LEFT JOIN zxxj_check_item_score cis ON cis.check_item_id = tci.checkItemId WHERE 1 = 1
AND cis.batch_id = #{batchId}
AND cis.score IS NOT NULL AND cis.score_info IS NOT NULL
AND cis.score IS NULL AND cis.score_info IS NULL
AND cis.template_id = #{templateId}

SELECT batch_id AS batchId,template_id AS templateId,weight,is_weight_average AS isWeightAverage,score,is_finish AS isFinish
from zxxj_score WHERE 1 =1 AND batch_id=#{batchId}

    AND template_id = #{templateId}

SELECT b.id AS batchId, b.`name` AS batchName, b.org_name, b.org_code, b.project_name, b.project_code, b.stage_name, b.stage_code, b.type, b.section_id, b.method, b.template_id AS templateId, t.type AS templateType, t.`name` AS templateName, b.assess_year, b.assess_month, b.enable, b.download_status, b.is_finish AS finish, b.score AS batchScore, b.create_user_name, b.create_user_id, b.finish_date FROM zxxj_batch b LEFT JOIN zxxj_template t ON t.id = b.template_id AND t.del_flag = 0 WHERE b.del_flag = 0
AND b.`name` = #{batchName}
AND b.id =#{batchId}
LIMIT 1

mapper.update:
UPDATE zxxj_score SET score = NULL WHERE
1 = 1
    AND batch_id =  #{batchId}

    AND template_id = #{templateId}

UPDATE zxxj_score SET is_finish = 0 WHERE
1 = 1
AND batch_id =  #{batchId}

    AND template_id = #{templateId}

UPDATE zxxj_check_item_score SET score = NULL,score_info = NULL,red_line_score=NULL,ext_score=NULL,pass_point=NULL,total_point=NULL
WHERE
1 = 1
    AND batch_id =  #{batchId}

    AND template_id = #{templateId}


    AND check_item_id IN

        #{id}

mapper.select:
SELECT r.ban_name AS banName, r.ban_code AS banCode, r.unit, r.floor, r.rooms, r.room_name AS roomName, r.room_code AS roomCode, ht.image_url AS checkImageUrl, ht.id AS houseTypeId FROM room r LEFT JOIN house_type ht ON ht.id = r.project_house_type_id AND ht.del_flag = 0 WHERE r.del_flag = 0 AND r.ban_code IN ( SELECT ban_code FROM zxxj_section_scope WHERE 1 = 1 AND section_id = #{sectionId} )
AND unit IN ( SELECT unit FROM zxxj_section_scope WHERE 1 = 1 AND section_id = #{sectionId} )
AND r.ban_name = #{roomQuery.banName}
AND r.unit = #{roomQuery.unitName}
AND r.floor = #{roomQuery.floorName}
AND r.rooms = #{roomQuery.roomName}
AND ht.check_type=1 GROUP BY r.room_code

SELECT id FROM zxxj_order t WHERE 1 = 1

    and t.org_name = #{orderQuery.orgName}


    and t.project_name = #{orderQuery.projectName}


    and t.stage_code = #{orderQuery.stageCode}


    and t.ban_name = #{orderQuery.banName}


    and t.unit = #{orderQuery.unit}


    and t.floor = #{orderQuery.floor}


    and t.room_name like concat('%',#{orderQuery.roomName},'%')


    and t.provider_name = #{orderQuery.providerName}


    and t.batch_id = #{orderQuery.batchId}


    and t.location like concat('%',#{orderQuery.lastCheckName},'%')


    and t.creator like concat('%',#{orderQuery.creatorName},'%')


    and t.create_date > #{orderQuery.submitTimeStart}


    and t.create_date < #{orderQuery.submitTimeEnd}


    and t.update_date > #{orderQuery.updateTimeStart}


    and t.update_date < #{orderQuery.updateTimeEnd}


    and t.`status` IN

        #{item}


and t.del_flag = 0
order by t.create_date desc
